from flask import Flask, request, send_file
import os

app = Flask(__name__)

ALLOWED_EXTENSIONS = {'txt', 'pdf', 'jpg', 'jpeg', 'png'}  # Add allowed extensions

def allowed_file(filename):
    return '.' in filename and filename.rsplit('.', 1)[1].lower() in ALLOWED_EXTENSIONS

@app.route('/api/read_file')
def read_file():
    filename = request.args.get('filename')

    if not filename:
        return "Filename parameter is required", 400

    # Sanitize the filename to prevent path traversal
    base_dir = 'files'  # Define a base directory for files
    if not os.path.exists(base_dir):
        os.makedirs(base_dir)

    filepath = os.path.join(base_dir, filename)

    if not allowed_file(filename):
        return "Invalid file extension", 400

    # Check if the file exists
    if not os.path.exists(filepath):
        return "File not found", 404

    # Check if the file is within the base directory (path traversal prevention)
    if not os.path.abspath(filepath).startswith(os.path.abspath(base_dir)):
        return "Path traversal attempt detected", 403

    try:
        return send_file(filepath, as_attachment=True)
    except Exception as e:
        return str(e), 500

if __name__ == '__main__':
    app.run(debug=True)
